import React, { Component } from "react";

class Items extends Component {
  state = {
    buttons: [
      { button1ID: 1, value: 5 },
      { button2ID: 2, value: 0 },
      { button3ID: 3, value: 0 },
      { button4ID: 4, value: 0 },
      { button5ID: 5, value: 0 },
      { button6ID: 6, value: 0 },
      { button7ID: 7, value: 0 },
      { button8ID: 8, value: 0 }
    ]
  };
  render() {
    return (
      <React.Fragment>
        <div className="campsiteItemsTitle">
          <h2>Campsite Items</h2>
        </div>
        <div className="campsiteItemsDiv">
          <h4>Tent</h4>
          <h4>{this.props.tentItems}</h4>
          <button
            id={1}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Tent
          </button>

          <h4>Sleeping bag</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Sleeping bag
          </button>
          <h4>Sleeping Pad</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Sleeping Pad
          </button>
          <h4>Camping Pillow</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Camping Pillow
          </button>
          <h4>Flashlight</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Flashlight
          </button>
          <h4>Camping Chair</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Camping Chair
          </button>
          <h4>Lantern</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Lantern
          </button>
        </div>
      </React.Fragment>
    );
  }
}

export default Items;
