import React, { Component } from "react";
import "./App.css";
import Title from "./Components/Title";
import CampsiteItems from "./Components/CampsiteItems";
import TotalNumberOfItems from "./Components/TotalNumberOfItems";

class App extends Component {
  state = {
    total: 0,
    buttons: [
      { id: 1, value: 4 },
      { id: 2, value: 0 },
      { id: 3, value: 0 },
      { id: 4, value: 0 },
      { id: 5, value: 0 },
      { id: 6, value: 0 },
      { id: 7, value: 0 },
      { id: 8, value: 0 }
    ]
  };

  handleIncrementTotal = () => {
    this.setState({ total: this.state.total + 1 });
    console.log(this.state.total);
  };

  handleIncrementCampsiteItem = () => {
    const campSiteItemTotal = this.state.buttons.value;
    campSiteItemTotal.setState({ value: this.state.buttons.value + 1 });
  };

  render() {
    return (
      <div>
        <Title />
        <TotalNumberOfItems numberTotal={this.state.total} />
        <CampsiteItems
          id={this.state.buttons.id}
          value={this.state.buttons.value}
          onHandleIncrementTotal={this.handleIncrementTotal}
          onHandleIncrementCampsiteItem={this.handleIncrementCampsiteItem}
          campsiteNumberTotal={this.state.buttons.value}
        />
      </div>
    );
  }
}

export default App;
