import React, { Component } from "react";

class ChuckNorris extends Component {
  constructor(props) {
    super(props);
    this.state = {
      brand: "Ford"
    };
  }

  changeColor = () => {
    this.state({ brand: "Chevy" });
  };

  render() {
    return (
      <div>
        <h3>My Car is a {this.state.brand}</h3>
        <button type="button" onClick={this.changeColor}>
          ChangeColor
        </button>
      </div>
    );
  }
}

export default ChuckNorris;
