import React, { Component } from "react";
import "../App.css";
class Counter extends Component {
  state = {
    count: 0
  };
  render() {
    this.getButtonClasses();
    return (
      <div>
        <span className="buttonTitle">{this.formatCount()}</span>
        <button className="this.getButtonClasses()">Increment Count</button>
      </div>
    );
  }

  getButtonClasses() {
    let classes = "buttonPressed";
    classes += this.state.count === 5 ? "button" : "buttonPressed";
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}
export default Counter;
