import React, { Component } from "react";
import "../App.css";
class Counter extends Component {
  state = {
    count: this.props.value,
    tags: ["tag1", "tag2", "tag3"]
  };

  // constructor(props) {
  //  super(props);
  //  this.handleIncrement = this.handleIncrement.bind(this);
  // }
  //OR

  handleIncrement = product => {
    this.setState({ count: this.state.count + 1 });
    console.log("Increment Clicked", this.state.count);
  };

  renderTags() {
    if (this.state.tags.length === 0) return <p>There are no Tags</p>;
    return (
      <ul>
        {this.state.tags.map(tag => (
          <li key={tag}>{tag}</li>
        ))}
      </ul>
    );
  }
  render() {
    let title = "buttonTitle";
    title = this.state.count === 0 ? "buttonTitle" : "buttonTitleNotZero";
    return (
      <div>
        <button className="resetButton">{this.handleReset}</button>
        <span className={title}>{this.formatCount()}</span>
        <button
          onClick={() => this.handleIncrement({ id: 1 })}
          className="button"
        >
          Increment Count
        </button>
        {this.state.tags.length === 0 && <p>Please create tags</p>}
        {this.renderTags()}
        <button
          onClick={() => this.props.onDelete(this.props.id)}
          className="deleteButton"
        >
          Delete
        </button>
      </div>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}
export default Counter;
