import React, { Component } from "react";
import "../App.css";
class Counter extends Component {
  state = {
    count: 0,
    tags: ["tag1", "tag2", "tag3"]
  };
  render() {
    let classesButtonTitle = "buttonTitle";
    let classesButton = "buttonPressed";
    classesButtonTitle +=
      this.state.count === 0 ? "buttonTitle" : "buttonTitleNotZero";
    classesButton += this.state.count === 0 ? "button" : "buttonPressed";
    return (
      <div>
        <span className="classesButtonTitle">{this.formatCount()}</span>
        <button className="classesButton">Increment Count</button>
        <ul>
          {this.state.tags.map(tag => (
            <li key={tag}>{tag}</li>
          ))}
        </ul>
      </div>
    );
  }

  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}
export default Counter;
