import React, { Component } from "react";
import "./App.css";
import Title from "./Components/Title";
import CampsiteItems from "./Components/CampsiteItems";
import TotalNumberOfItems from "./Components/TotalNumberOfItems";

class App extends Component {
  state = {
    total: 0,
    tentTotal: 0,
    sleepingBagTotal: 0,
    sleepingPadTotal: 0,
    campingPillowTotal: 0,
    flashlightTotal: 0,
    campingChairTotal: 0,
    lanternTotal: 0
  };

  handleIncrementTotal = e => {
    console.log(e);
    this.setState({ total: this.state.total + 1 });
    if (this.id === 1) {
      this.setState({ tentTotal: this.state.tentTotal + 1 });
    } else {
      console.log("not the tent");
    }

    console.log(this.state.total);
  };

  render() {
    return (
      <div>
        <Title />
        <TotalNumberOfItems numberTotal={this.state.total} />
        <CampsiteItems
          id={this.state.buttons.id}
          value={this.state.buttons.value}
        />
      </div>
    );
  }
}

export default App;
