import React, { Component } from "react";

class Items extends Component {
  state = {};
  render() {
    return (
      <React.Fragment>
        <div className="campsiteItemsTitle">
          <h2>Campsite Items</h2>
        </div>
        <div className="campsiteItemsDiv">
          <h4>Tent</h4>
          <h4>{this.props.value}</h4>
          <button
            id="tentButton"
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Tent
          </button>

          <h4>Sleeping bag</h4>
          <h4>{0}</h4>
          <button
            id="sleepingBagButton"
            value="0"
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Sleeping bag
          </button>
          <h4>Sleeping Pad</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Sleeping Pad
          </button>
          <h4>Camping Pillow</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Camping Pillow
          </button>
          <h4>Flashlight</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Flashlight
          </button>
          <h4>Camping Chair</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Camping Chair
          </button>
          <h4>Lantern</h4>
          <h4>{0}</h4>
          <button
            id={this.props.id}
            value={this.props.value}
            className="button"
            onClick={this.props.onHandleIncrementTotal}
          >
            Add Lantern
          </button>
        </div>
      </React.Fragment>
    );
  }
}

export default Items;
