import React, { Component } from "react";
import "./App.css";
import Title from "./Components/Title";
import CampsiteItems from "./Components/CampsiteItems";
import TotalNumberOfItems from "./Components/TotalNumberOfItems";

class App extends Component {
  state = {
    total: 0,
    tentTotal: 0,
    sleepingBagTotal: 0,
    sleepingPadTotal: 0,
    campingPillowTotal: 0,
    flashlightTotal: 0,
    campingChairTotal: 0,
    lanternTotal: 0,
    buttons: [
      { tentButton: 1, value: 5 },
      { button2ID: 2, value: 0 },
      { button3ID: 3, value: 0 },
      { button4ID: 4, value: 0 },
      { button5ID: 5, value: 0 },
      { button6ID: 6, value: 0 },
      { button7ID: 7, value: 0 },
      { button8ID: 8, value: 0 }
    ]
  };

  handleIncrementTotal = id => {
    console.log(id.currentTarget.value);
    this.setState({ total: this.state.total + 1 });

    console.log(this.state.total);
  };

  render() {
    return (
      <div>
        <Title />
        <TotalNumberOfItems numberTotal={this.state.total} />
        <CampsiteItems
          onHandleIncrementTotal={() => this.handleIncrementTotal(id)}
          tentItems={this.state.tentTotal}
        />
      </div>
    );
  }
}

export default App;
